const items = $input.all();
console.log("items", items);
const all = items.map(i => i.json)
const startPackage = $('Package Eingabe').first().json['Package Name']

let md = ''
const typeLabels = {
  CLAS: 'Classes',
  INTF: 'Interfaces',
  TABL: 'Dictionary Elements',
  PROG: 'Programs',
  FUGR: 'Function Groups',
  TRAN: 'Transactions',
  ENHO: 'Enhancements',
  DEVC: 'Packages',
  DTEL: 'Data Elements',
  DOMA: 'Domains',
  MSAG: 'Message class'
}

const writeSentence = (sentence) => {
  if(sentence.trim().endsWith(".")) {
    return sentence;
  } else {
    return sentence + ".";
  }
}

const startPackageObj =  items.map(i => i.json).find(x => x.obj_name === startPackage && x.object === 'DEVC');
const data = all.filter(x => x.obj_name !== startPackage && x.object !== 'DEVC');

const typeOrder = ['CLAS', 'INTF', 'TABL']

const grouped = {}

for (const { devclass, object, obj_name, description } of data) {
  if (!grouped[devclass]) grouped[devclass] = {}
  if (!grouped[devclass][object]) grouped[devclass][object] = []
  grouped[devclass][object].push({ obj_name, object, description })
}

const countPackages =  items.map(i => i.json).filter(x => x.object === 'DEVC');
const countobjects =  items.map(i => i.json).filter(x => x.object !== 'DEVC');

md += '# Technical documentation of ' + startPackage + '\n\n'

if(startPackageObj && startPackageObj.description) {
  md += writeSentence(startPackageObj.description) + '\n\n';
}

if(countPackages.length > 0) {
md += 'This technical documentation contains ' + countPackages.length + ' sub packages and '+ countobjects.length + ' development objects.\n\n';
} else {
  md += 'This technical documentation contains ' + countobjects.length + ' development objects.\n\n';
}

for (const pkg in grouped) {
  md += '## üì¶ ' + pkg + '\n'

  const allobjects = Object.values(grouped[pkg]).flat()
  const firstWithdescription = allobjects.find(obj => obj.description)
  if (firstWithdescription) {
    md += writeSentence(firstWithdescription.description) + '\n\n'
  }

  const sortedTypes = Object.keys(grouped[pkg]).sort((a, b) => {
    const idxA = typeOrder.indexOf(a) === -1 ? 999 : typeOrder.indexOf(a)
    const idxB = typeOrder.indexOf(b) === -1 ? 999 : typeOrder.indexOf(b)
    return idxA - idxB || a.localeCompare(b)
  })

  for (const type of sortedTypes) {
    const label = typeLabels[type] || type
    md += '### ' + label + '\n'
    const entries = grouped[pkg][type].sort((a, b) => a.obj_name.localeCompare(b.obj_name))
    for (const { obj_name, object, description } of entries) {
      const name = `**${obj_name}**`;
      md += '- ' + name + (description ? ' - ' + description : '') + '\n';
    }
    md += '\n'
  }
}

return [{ json: { toc: md, package: startPackage } }]

const allDocs = $input.all().map(x => x.json.output).join("\n\n");
const toc = $('Inhaltsverzeichnis').first().json.toc;

return {
  merged: toc + "\n\n" + allDocs
}
